Users - CREATE TABLE users(id SERIAL PRIMARY KEY, first_name VARCHAR(60) NOT NULL, last_name VARCHAR(120) NOT NULL, email VARCHAR(120) NOT NULL UNIQUE, password VARCHAR(120) NOT NULL, created_on DATE NOT NULL DEFAULT CURRENT_DATE, payment_id INTEGER REFERENCES payments(id), film_id INTEGER REFERENCES films(id));
Payments - CREATE TABLE payments(id SERIAL PRIMARY KEY, card_number VARCHAR(20) NOT NULL UNIQUE, expiry_date DATE NOT NULL, security_code VARCHAR(5) NOT NULL, billing_address VARCHAR(300) NOT NULL);
Films - CREATE TABLE films(id SERIAL PRIMARY KEY, name VARCHAR(300) NOT NULL, genres TEXT[] NOT NULL, actors TEXT[] NOT NULL, directors TEXT[], date SMALLINT NOT NULL);
Basket - CREATE TABLE basket(id SERIAL PRIMARY KEY, users_id INTEGER REFERENCES users(id) ON DELETE CASCADE, films_id INTEGER REFERENCES films(id) ON DELETE CASCADE);
